/** Get icon component by brand ID */
export function getBrandIcon(brandId: string): React.FC<BrandIconProps> {
    const normalized = brandId.toLowerCase().trim().replace(/[\s-]/g, '_');
    if (normalized === 'twitter' || normalized === 'x') return XIcon;
    return BRAND_ICONS[normalized] || ManualIcon;
}
